import React from "react";
import { useEffect, useState } from "react";
import { useHistory, useParams } from "react-router-dom";

function StudyCardFlip({ cards }) {
    const history = useHistory();
    const {deckId} = useParams();
    const state = {flipped: false, currentCard: 0}

    // const [cardCount, setCardCount] = useState(1);
    // const [sideOfCard, setSideOfCard] = useState(null);
    // const [studyCardCount, setStudyCardCount] = useState(0);
    const [session, setSession] = useState(state);
    // const orderedCards = currentCards.sort((a, b) => a.id - b.id);

    // useEffect(() => {
    //     if(cards.length > 2){
    //         setStudyCard(true);
    //     }
    //     setStudyCard(orderedCards[studyCardCount])
    // }, [currentCards])

    // const handleFlip = (e) => {
    //     e.preventDefault()
    //     setSideOfCard(!sideOfCard)
    // }


    const handleNext = () => {
        if (session.currentCard < cards.length - 1) {
            setSession({
                ...session,
                currentCard: session.currentCard + 1,
                flipped: false,
              });
            } else {
              window.confirm(
                'Restart cards? Click "cancel" to return to the home page.'
              )
                ? setSession(state)
                : history.push("/");
            }
        };

    const handleAddCards = (e) => {
        e.preventDefault()
        history.push(`/decks/${deckId}/cards/new`)
    }

    if (cards.length > 2) {
            return (
                <div>
                    <h5>Card {session.currentCard + 1} of {cards.length}</h5>
                    <br />
                    <p className="card-text">
              {session.flipped
                ? cards[session.currentCard].back
                : cards[session.currentCard].front} </p>
                    <button onClick={() => setSession({ ...session, flipped: true })}>

                    </button>
                </div>
            )
        }
        if (session.flipped){
            return (
                <div>
                    {/* <h5>Card {cardCount} of {currentCards.length}</h5>
                    {studyCard.back} */}
                    <br />
                    {/* <button onClick={handleFlip}>Flip</button> */}
                    <button onClick={handleNext}>Next</button> 
                </div>
            )
        }
        else {
            return (
                <div>
                    <h5>Not enough cards.</h5>
                    <p>You need at least 3 cards to study. There are {cards.length} in this deck.</p>
                    <button onClick={handleAddCards}> Add Cards</button>
                </div>
            )
        }
        // return (
        //     <p>Loading...</p>
        // )
    }



export default StudyCardFlip;
