import React from "react";
import { useEffect, useState } from "react";
import { useHistory, Link} from "react-router-dom";
import { deleteDeck, listDecks } from "../utils/api";

function Home({deck}) {
    const history = useHistory();
    const [decks, setDecks] = useState(null);

    useEffect(() => {
        async function loadDeck() {
              const getDecks = await listDecks();
              setDecks(getDecks);
        }
        loadDeck();
  }, []);


    const handleCreateDeck = () => {
        history.push("/decks/new")
    }

    const handleDelete = (event)=> {
        const abortController = new AbortController();
        event.preventDefault();
        if (window.confrim("Would you like to delete this Deck?")){
            deleteDeck(deck.id, abortController.signal)
            .then((response)=> {
                const tempDecks= decks.filter((x)=> x.id !==deck.id);
                setDecks(tempDecks);
                history.go("/");
            })
        }
        return () => abortController.abort();
    }


    if(decks){
        return (
            <div>
                <button onClick={handleCreateDeck}> Create Deck</button>
                <ul>
                    {decks.map((deck) => (
                        <li key={deck.id}>
                            <h2>{deck.name}</h2>
                            <p>{deck.cards.length} cards</p>
                            <p>{deck.description}</p>
                            <Link to={`/decks/${deck.id}`}> View! </Link>
                            <Link to={`/decks/${deck.id}/edit`}> Edit! </Link>
                            <Link to={`/decks/${deck.id}/study`}> Study! </Link>
                            <button 
                            onClick={handleDelete}> 
                            Delete 
                            </button>
                        </li> 
                    ))}
                </ul>
            </div>
        )
    }
    return (
        <p>Loading...</p>
    );
}

export default Home;
