import React from "react";
import { useEffect, useState } from "react";
import { Link, useHistory, useParams } from "react-router-dom";
import { readDeck } from "../utils/api";
import EditCard from "./EditCard";

function DeckInfo({deck,card, updateDecks} ) {
    const {deckId} = useParams();
    const history = useHistory();
    const [decks,setDecks]= useState()
    const [currentDeck, setCurrentDeck] = useState(null)
    const [currentCards, setCurrentCards] = useState(null);

    useEffect(loadDeck, [deckId]);

    function loadDeck() {
        readDeck(deckId).then(setDecks);
      }

      useEffect(() => {
        const abortController = new AbortController();
        const loadDeck = async () => {
          const loadedDeck = await readDeck(deckId, abortController.signal);
          setDecks(() => loadedDeck);
        };
        loadDeck();
        return () => abortController.abort();
      }, [deckId]);

    const handleDelete = async ()=> {
        const deleteDeck = window.confirm("would you like to Delete this deck?");
        if (deleteDeck){
            await deleteDeck(deck.id);
            updateDecks(-1);
            history.push("/");
        }
    }

    const handleCard = async()=> {
        if(EditCard(currentDeck.id))
            history.push(`/decks/${currentDeck.id}/cards/${card.id}/edit`);
        }


    if (currentDeck && currentCards) {
        return (
            <div>
                <div>
                    <Link to="/">Home</Link> / {currentDeck.name}
                </div>
                <div>
                    <h2>{currentDeck.name}</h2>
                    <p>{currentDeck.description}</p>
                    <button onClick={() => {history.push(`/decks/${currentDeck.id}/edit`)}}>Edit</button>
                    <button onClick={() => {history.push(`/decks/${currentDeck.id}/study`)}}>Study</button>
                    <button onClick={() => {history.push(`/decks/${currentDeck.id}/cards/new`)}}>Add Cards</button>
                    <button onClick={handleDelete}>
                    Delete
                    </button>
                </div>
                <div>
                    <h3>Cards</h3>
                    <ul>
                        {currentCards.map((card) => (
                            <li key={card.id}>
                                {card.front}
                                <div>{card.back}</div>
                                <button onClick={handleCard}>Edit</button>
                                <button onClick={handleDelete}>Delete</button>
                            </li>
                        ))}
                    </ul>
                </div>
            </div>
        );
    };
    return (
        <p>Loading...</p>
    );
};

export default DeckInfo;
