            setDecks([]);
            try {
              const response = await listDecks()
              setDecks(response);
            } catch (error) {console.log(error)}
          }
          GetDecks();
    }, [])

    const handleCreateDeck = () => {
        history.push("/decks/new")
    }

const handleDelete = ()=> {
