import React from "react";
import { useEffect, useState } from "react";
import { Link, useHistory, useParams } from "react-router-dom";
import { deleteCard, readDeck, updateDecks } from "../utils/api";

function DeckInfo({deck}) {
    const {deckId} = useParams();
    const history = useHistory();

    const [currentDeck, setCurrentDeck] = useState(null)
    const [currentCards, setCurrentCards] = useState(null);

    useEffect(() => {
        const abortController = new AbortController();
        const newDeck= async()=> {
            const deckA= await readDeck(deckId, abortController.signal)
            setCurrentDeck(()=> deckA);
            setCurrentCards(()=> deckA);
        }
        newDeck()
        return ()=> abortController.abort();
    }, [currentDeck,currentCards,deckId])

    const handleDelete = async ()=> {
        const deleteDeck = window.confirm("would you like to Delete this deck?");
        if (deleteDeck){
            await deleteDeck(deck.id);
            updateDecks(-1);
            history.push("/");
        }
    }

    if (currentDeck && currentCards) {
        return (
            <div>
                <div>
                    <Link to="/">Home</Link> / {currentDeck.name}
                </div>
                <div>
                    <h2>{currentDeck.name}</h2>
                    <p>{currentDeck.description}</p>
                    <button onClick={() => {history.push(`/decks/${currentDeck.id}/edit`)}}>Edit</button>
                    <button onClick={() => {history.push(`/decks/${currentDeck.id}/study`)}}>Study</button>
                    <button onClick={() => {history.push(`/decks/${currentDeck.id}/cards/new`)}}>Add Cards</button>
                    <button onClick={handleDelete}>
                    Delete
                    </button>
                </div>
                <div>
                    <h3>Cards</h3>
                    <ul>
                        {currentCards.map((card) => (
                            <li key={card.id}>
                                {card.front}
                                <div>{card.back}</div>
                                <button onClick={() => {history.push(`/decks/${currentDeck.id}/cards/${card.id}/edit`)}}>Edit</button>
                                <button onClick={() => {
                                    if(window.confirm("Delete this card?")){
                                        deleteCard(`${card.id}`);
                                        history.push(`/decks/${deckId}`)
                                    }}}>
                                Delete
                                </button>
                            </li>
                        ))}
                    </ul>
                </div>
            </div>
        );
    };
    return (
        <p>Loading...</p>
    );
};

export default DeckInfo;
