function Study(){
    const [deck,setDeck] = useState([]);
    const {deckId} = useParams();
    useEffect(() => {
      const loadDeck = async () => {
        const newDeck = await readDeck(deckId);
        setDeck(() => newDeck);
      };
      loadDeck();
    }, [deckId]);


    if(Object.keys(deck).length){
        return (
            <div>
                <div>
                    <Link to="/">Home</Link> / <Link to={`/decks/${deck.id}`}>{deck.name}</Link> / Study
                </div>
                <div>
                    <h1>{deck.name}: Study</h1>
                </div>
                    <StudyCardFlip cards={deck.cards} />
            </div>
        )

    }
    return (
        <p>Loading...</p>
    );
}
export default Study;
