import React from "react";
import { useEffect, useState } from "react";
import { useHistory, useParams } from "react-router-dom";
import { Link } from "react-router-dom";
import { createCard, readDeck } from "../utils/api";

function CardNew() {
    const params = useParams();
    const history = useHistory();

    const state = {front:"", back: ""};
    const [cardData, setCardData]= useState(state);
    const [deck, setDeck] = useState(null)


    useEffect(() => {
        const abortController = new AbortController();
       const loadDeck= async ()=> {
                const newDeck = await readDeck(params, abortController.signal);
                setDeck(()=> newDeck);
                setCardData({
                    front:newDeck.front,
                    back: newDeck.back,
                })
        }
        loadDeck()
        return () => abortController.abort();
    }, [params]);

    const handleDone = (e) => {
        e.preventDefault();
        history.push(`/decks/${params.deckId}`)
    }

    const handleSubmit = (event) => { 
        event.preventDefault();
        const card = {cardData}
        createCard(params.deckId, card)
        .then(response => {
            console.log(response)
            //history.push("/decks/:deckId/cards/new")
        })
    }




    if(deck){
        return (
            <div>
                <div>
                    <Link to="/">Home</Link> / <Link to={`/decks/${params.deckId}`}>{cardData.name}</Link> / <div>Add Card</div>
                </div>
                <div>
                    <h2>{cardData.name}: Add Card</h2>
                </div>
                <form onSubmit={handleSubmit}>
                    <label>Front</label>
                    <br />
                    <textarea
                    required
                    value={cardData.front}
                    onChange={(e) => setCardData(e.target.value)}
                    />
                    <br />
                    <label>Back</label>
                    <br />
                    <textarea
                    required
                    value={cardData.back}
                    onChange={(e) => setCardData(e.target.value)}

                    />
                    <br />
                    <button onClick={handleDone}>Done</button>
                    <button type="submit">Save</button>
                </form>
            </div>
        )
    }
    return <p>Loading...</p>
}
export default CardNew;
