import React from "react";
import { useEffect, useState } from "react";
import { Link, useHistory, useParams } from "react-router-dom";
import { readDeck, updateCard } from "../utils/api";

function EditCard() {
    const history = useHistory();
    const params = useParams();
    const state = {front: "", back: ""}
    const [updatedCard, setUpdatedCard]= useState(state)

    const [updatedDeck, setUpdatedDeck]= useState({});

    // const [front, setFront] = useState("")
    // const [back, setBack] = useState("")
    // const [currentDeck, setCurrentDeck] = useState(null)
    // const [currentCard, setCurrentCard] = useState(null)

useEffect (()=> {
    const abortController= new AbortController();
    const loadInfo = async ()=> {
        const newDeck = await readDeck(params, abortController.signal);
        setUpdatedDeck(()=> newDeck);
        setUpdatedCard({
            id:params,
            front: newDeck.front,
            back:newDeck.back,
        })
    }
    loadInfo()
    return ()=> abortController.abort();
}, [params])


    useEffect(() => {
        async function loadInfo(){
            try {
                const response = await readDeck(params.deckId);
                setUpdatedDeck(response)
                setUpdatedCard(response.cards.find((card) => (card.id + "") === params.cardId))
                updatedCard((response.cards.find((card) => (card.id + "") === params.cardId)).front)
                //setBack((response.cards.find((card) => (card.id + "") === params.cardId)).back)
            } catch (error) {console.log(error)}
        }
        loadInfo()
    }, [params])

    const handleCancel = (e) => {
        e.preventDefault();
        history.push("/")
    }
    const handleSubmit = (e) => {
        e.preventDefault()
        const card = {
            ...updatedCard,
        }   
        updateCard(card)
        .then(response => {
            console.log(response);
            setUpdatedCard(response)
            history.push(`/decks/${params.deckId}`)
        })
    }
    // const handleSubmit = async(e) => {
    //     e.preventDefault();
    //     const deck = await updateCard(updatedCard);
    //         history.push(`/decks/${params.deck}`)
    //     }


    if(updatedCard && updatedDeck){
        return (
            <div>
                <div>
                    <Link to="/">Home</Link> / <Link to={`/decks/${params.deckId}`}>{updatedDeck.name}</Link>  / Edit Card {updatedCard.id}
                </div>
                <h2>Edit Card</h2>
                <form onSubmit={handleSubmit}>
                    <label>Front</label>
                    <br />
                    <textarea
                    required
                    value={updatedCard.front}
                    onChange={(e) => setUpdatedCard(e.target.value)}
                    />
                    <br />
                    <label>Back</label>
                    <br />
                    <textarea
                    required
                    value={updatedCard.back}
                    onChange={(e) => setUpdatedCard(e.target.value)}
                    />
                    <br />
                    <button onClick={handleCancel}>Cancel</button>
                    <button type="submit">Submit</button>
                </form>
            </div>
        )
    }
    return <p>Loading...</p>
}
export default EditCard;
